import React, { useState, useEffect } from "react";
import "./Slider.css";
import BtnSlider from "./BtnSlider";
import dataSlider from "./dataSlider";

export default function Slider() {
  const [slideIndex, setSlideIndex] = useState(1);
  const [dotIndex, setDotIndex]= useState();
  

  const moveDot = (index) => {
    setSlideIndex(index);
    
  };
  
    
    
  

  const [currentSlide, setCurrentSlide] = useState(0);
  const slideLength = dataSlider.length;

  const autoScroll = true;
  let slideInterval;
  let intervalTime = 5000;

  const nextSlide = () => {
    setCurrentSlide(currentSlide === slideLength - 1 ? 0 : currentSlide + 1);
    console.log("next");
  };
  function auto() {
    slideInterval = setInterval(nextSlide, intervalTime);
    
  }
  useEffect(() => {
    setCurrentSlide(0);
  }, []);

  useEffect(() => {
    if (autoScroll) {
      auto();
    }
    return () => clearInterval(slideInterval);
  
  }, [currentSlide]);

  return (
    <div className="container-slider">
      {dataSlider.map((obj, index) => {
        return (
          <div
            key={obj.id}
            className={currentSlide === index  ? "slide active-anim" : "slide"}
          >
            <img src={process.env.PUBLIC_URL + `/Imgs/img${index + 1}.jpg`} />
          </div>
        );
      })}

      <div className="container-dots">
        {Array.from({ length: 5 }).map((item, index) => (
          <div
            onClick={() => moveDot(index + 1)}
            className={/* slideIndex === index ? "dot active" : "dot" */  dotIndex === index ? "dot active" : "dot"}
            
          ></div>
        ))}
      </div>
    </div>
  );
}
